<?php/* Template Name: Login Template */get_header();?><script type="text/javascript">	jQuery(document).ready(function(){				jQuery('#loading').hide();		jQuery("#loginBtn").click(function() {							var input_data = jQuery('#loginForm').serialize();			var logUser = jQuery('#username').val();			var logPass = jQuery('#password').val();						if(logUser == '' && logPass != ''){ jQuery('.error').show(); jQuery('.error').html('Your Username is empty!'); return false; }			if(logPass == '' && logUser != ''){ jQuery('.error').show(); jQuery('.error').html('Your Password is empty!'); return false; }			if(logUser == '' && logPass == ''){ jQuery('.error').show(); jQuery('.error').html('Your Username and Password is empty!'); return false; }									jQuery('#loading').show();			jQuery.ajax({				type: "POST",				url: "<?php echo site_url('wp-login.php','login_post'); ?>",				data: input_data,				success: function(msg) {				// if login incorrect, wordpress will redirect user to its own login form. We scan for an error term.				var reg1 = /login_error/g;				if (reg1.test(msg)) {					jQuery('.error').show();					jQuery('.error').html("<?php _e('Your login  is not correct. Please try again.'); ?>");					jQuery('#loading').hide();					//jQuery('#message').html("<?php //_e('login  is not correct. Please try again.'); ?>");				}				else {											// login success. redirect users to some page.					jQuery(location).attr('href', '<?php echo home_url( '/profile/' ); ?>');				}				}			});				return false;		});	});</script><link href="<?php bloginfo('template_url'); ?>/css/page-style.css" rel="stylesheet" type="text/css"><!-- InstanceBeginEditable name="Main Container" --><!-- Main Container========================================================--><div id="mainContainer"><div id="careers" class="details"><?php if (function_exists('qt_custom_breadcrumbs')) qt_custom_breadcrumbs(); ?><!-- Side Navigation========================================================--><div id="sideNav"><?php get_sidebar(); ?></div><!--sideNav--><!-- Main Contents========================================================--><div id="mainContents"><section><header><h2 class="titleStyle_A2"><?php echo $postTitle; ?><a class="btn" href="<?php echo home_url(); ?>"><img src="<?php bloginfo('template_url'); ?>/images/btn/btn_contact.png" alt="Contact RIKEN BSI for Careers" height="27"></a></h2></header><section><div class="txt"><?php if(!is_user_logged_in()) { ?>                        <div class="login_dropdown">							<div style="display:none;" id="loading">Loading...</div>							<div style="display:none;" class="error"></div>                            <form id="loginForm" action="" method="post" autocomplete="off">                                <label>                                    <img src="<?php bloginfo('template_url'); ?>/images/user.png" alt="" />                                    <input type="text" name="log" id="username" placeholder="Username/Email/MemberID"class="sn-input" value="<?php if ( isset( $user_login) ) echo esc_attr(stripslashes($user_login)); ?>" />                                </label>                                <label>                                    <img src="<?php bloginfo('template_url'); ?>/images/lock_white.png" alt="" />                                    <input type="password" name="pwd" id="password" placeholder="Password" class="sn-input" value="" />                                </label>                                <button type="submit" name="signIn" id="loginBtn" class="sign_in_btn">Sign In</button>								                                                                <table>                                    <tr>                                        <td>                                            <input type="checkbox" name="checkboxG5" id="checkboxG5" class="css-checkbox" checked="checked"/>                                            <label for="checkboxG5" class="css-label">Remember Me</label>                                         </td>                                        <td>                                            <a href="<?php echo home_url('/password-reset'); ?>">Forget Password ?</a>                                        </td>                                    </tr>                                </table>                            </form>                        </div>                                                    <?php } else { ?>						<div class="logout_dropdown">	                           	                            <p style="margin-top:12px">Welcome</p>                             <p class="loged_user">Hello, <a href="<?php echo home_url('/profile'); ?>" target="_self" title="<?php echo $current_user->display_name; ?>" alt="<?php echo $current_user->display_name; ?>"><?php echo $current_user->display_name; ?></a></p>                                                            <p class="log_out">                                <a href="<?php echo wp_logout_url( get_permalink() ); ?>">Log Out</a>                            </p>                        </div>						<?php } ?></div></section></section></div><!--/mainContents--><div class="cl"></div></div></div><!--/mainContainer--><!-- InstanceEndEditable --><?php get_footer(); ?>